"use strict";(self.webpackChunkdf_doc_source=self.webpackChunkdf_doc_source||[]).push([[127],{2294:n=>{n.exports=JSON.parse('{"blogPosts":[{"id":"motivation","metadata":{"permalink":"/fluffy-octo-robot/blog/motivation","editUrl":"https://github.com/facebook/docusaurus/tree/main/packages/create-docusaurus/templates/shared/blog/motivation.md","source":"@site/blog/motivation.md","title":"\u5f00\u53d1\u76ee\u7684","description":"\u6211\u7684\u9700\u6c42","date":"2023-03-15T19:25:06.000Z","formattedDate":"2023\u5e743\u670815\u65e5","tags":[{"label":"motivation","permalink":"/fluffy-octo-robot/blog/tags/motivation"}],"readingTime":4.6,"hasTruncateMarker":false,"authors":[{"name":"Ce Wang","title":"Solo dev","url":"https://github.com/haloWang","imageURL":"https://github.com/haloWang.png","key":"halowang"}],"frontMatter":{"slug":"motivation","title":"\u5f00\u53d1\u76ee\u7684","authors":["halowang"],"tags":["motivation"]}},"content":"## \u6211\u7684\u9700\u6c42\\n\\n\u4ee5\u4e0b\u662f\u6211\u7684\u4e24\u4e2a\u5e38\u7528\u9700\u6c42\uff1a\\n\\n- \u9605\u8bfb\u548c\u7406\u89e3\u6d4f\u89c8\u5668\u4e2d\u7684\u82f1\u6587\u5185\u5bb9\uff0c\u5982 github\uff0cstackoverflow\uff0creddit\\n- \u5728\u684c\u9762\u5e73\u53f0\u4e0a\u9605\u8bfb\u548c\u7406\u89e3\u4e00\u4e9b\u4ece\u672a\u88ab\u7ffb\u8bd1\u6210\u6c49\u8bed\u7684\u82f1\u6587\u4e66\u7c4d\uff0c\u8fd9\u4e9b\u4e66\u7c4d\u7684\u5927\u90e8\u5206\u4e3a PDF \u6587\u4ef6\\n\\n## \u6211\u9047\u5230\u7684\u56f0\u96be\\n\\n\u4f46\u5728\u8fdb\u884c\u4e0a\u8ff0\u7684\u9605\u8bfb\u7406\u89e3\u6d3b\u52a8\u65f6\uff0c\u6211\u9047\u5230\u4e86\u4e00\u4e9b\u56f0\u96be\uff1a\\n\\n- \u6211\u7ecf\u5e38\u5fd8\u8bb0\u82f1\u8bed\u5355\u8bcd\u7684\u610f\u601d\uff0c\u6216\u4e0d\u6562\u786e\u8ba4\u4e00\u4e2a\u5355\u8bcd\u7684\u610f\u601d\u3002\u751a\u81f3\uff0c\u6211\u7ecf\u5e38\u4f1a\u5fd8\u8bb0 30 \u79d2\u4e4b\u524d\u521a\u521a\u67e5\u8fc7\u7684\u5355\u8bcd\\n- \u5f53\u524d\u5404\u79cd\u67e5\u5355\u8bcd\u7684\u5e94\u7528\u7a0b\u5e8f\u548c\u7f51\u9875\u5bf9\u4e8e\u6211\u6765\u6765\u8bf4\u90fd\u6709\u4e9b\u7e41\u7410\uff0c\u6211\u9700\u8981\u7b49\u5f85\u8bcd\u5178\u8f6f\u4ef6\u7684\u7f51\u7edc\u8bf7\u6c42\uff0c\u8fc7\u6ee4\u8bcd\u5178\u8f6f\u4ef6\u4e2d\u7684\u5e7f\u544a\u3002\u6211\u8fd8\u8981\u4fdd\u8bc1\u81ea\u5df1\u5728\u7e41\u7410\u7684\u64cd\u4f5c\u6d41\u7a0b\u4e2d\u4e0d\u4f1a\u5206\u5fc3\\n- \u4f7f\u7528\u5168\u6587\u7ffb\u8bd1\u8f6f\u4ef6\uff0c\u4f1a\u5bfc\u81f4\u76f8\u5f53\u4e00\u90e8\u5206\u7684\u4fe1\u606f\u4e22\u5931\u6216\u504f\u79bb\u539f\u672c\u542b\u4e49\uff0c\u6211\u9700\u8981\u5bf9\u5168\u6587\u7ffb\u8bd1\u8f6f\u4ef6\u7ea0\u9519\uff0c\u6216\u8005\u76f4\u63a5\u67e5\u770b\u539f\u6587\u624d\u80fd\u4e86\u89e3\u5230\u771f\u6b63\u7684\u91ca\u4e49\\n\\n\u4e3e\u4e2a\u4f8b\u5b50\uff0c\u6211\u4e0d\u77e5\u9053 \u201cslipper\u201d \u7528\u82f1\u8bed\u600e\u4e48\u8bf4\u3002\u4f46\u662f\uff0c\u5f53\u8fd9\u4e2a\u8bcd\u5728\u82f1\u8bed\u8bed\u6599\u4e2d\u51fa\u73b0\u65f6\uff0c\u5b83\u5c06\u4f60\u7684\u601d\u7eea\u4ece \u201c\u601d\u8003\u4f5c\u8005\u8868\u8fbe\u7684\u542b\u4e49\u201d\uff0c\u5f3a\u884c\u8f6c\u53d8\u4e3a \u201c\u601d\u8003 slipper \u662f\u4ec0\u4e48\u610f\u601d\u201d\u3002\u5f53\u6211\uff0c\u67e5\u8be2\u5230\u4e86 \u201cslipper\u201d \u7684\u610f\u601d\u540e\uff0c\u5728\u8fd4\u56de\u6765\u5c06\u6ce8\u610f\u529b\u6295\u5165\u4e4b\u524d\u7684\u4e0a\u4e0b\u6587\u3002\u8fd9\u4e2a\u8fc7\u7a0b\u8017\u65f6\uff0c\u5bb9\u6613\u51fa\u9519\uff0c\u6b63\u53cd\u9988\u51e0\u4e4e\u6ca1\u6709\u3002\\n\\n\u6211\u8ba4\u4e3a\uff0c\u8fd9\u4e9b\u56f0\u96be\u589e\u52a0\u4e86\u6211\u4ece\u4e8b\u8111\u529b\u6d3b\u52a8\u65f6\u7684\u5fc3\u667a\u8d1f\u62c5\uff0c\u964d\u4f4e\u4e86\u6211\u7684\u9605\u8bfb\u6548\u7387\u3002\u751a\u81f3\uff0c\u76f4\u63a5\u5bfc\u81f4\u6211\u5206\u5fc3\uff0c\u7cbe\u795e\u4e0d\u96c6\u4e2d\uff0c\u6700\u7ec8\u653e\u5f03\u9605\u8bfb\uff0c\u653e\u5f03\u83b7\u53d6\u77e5\u8bc6\u3002\\n\\n\u9762\u5bf9\u4e0a\u8ff0\u7684\u56f0\u96be\uff0c\u6211\u4eec\u901a\u5e38\u7684\u89e3\u51b3\u529e\u6cd5\u90fd\u662f\u201c\u80cc\u5355\u8bcd\u201d\u3002\u53ef\u662f\uff0c\u80cc\u5355\u8bcd\u5bf9\u4e8e\u6211\u6765\u8bf4\u662f\u4e2a\u75db\u82e6\u7684\u8fc7\u7a0b\uff1a\\n\\n- \u6211\u9700\u8981\u80cc\u8bf5\u5927\u91cf\u7684\uff0c\u4f7f\u7528\u9891\u7387\u6781\u4f4e\uff0c\u5e76\u4e14\u56e0\u4f7f\u7528\u9891\u7387\u4f4e\u800c\u975e\u5e38\u5bb9\u6613\u53d8\u5f97\u751f\u758f\u7684\u5355\u8bcd\\n- \u8003\u7eb2\u5185\u7684\u5355\u8bcd\u4ece\u6765\u90fd\u662f\u4e3a\u5927\u5bb6\u51c6\u5907\u7684\uff0c\u80cc\u8bf5\u8003\u7eb2\u5185\u7684\u5355\u8bcd\u4e0d\u80fd\u8986\u76d6\u5230\u4e13\u4e1a\u9886\u57df\u7684\u8bcd\u6c47\\n- \u5728\u6c49\u8bed\u73af\u5883\u4e2d\uff0c\u4fdd\u8bc1\u5927\u8111\u5bf9\u5927\u91cf\u82f1\u8bed\u5355\u8bcd\u7684\u719f\u6089\u548c\u5feb\u901f\u54cd\u5e94\uff0c\u9700\u8981\u82b1\u8d39\u5f88\u591a\u989d\u5916\u7684\u65f6\u95f4\u4e0e\u7cbe\u529b\\n\\n\u5bf9\u6211\u6765\u8bf4\uff0c\u80cc\u5355\u8bcd\u7684\u6548\u7387\u592a\u4f4e\u4e86\u3002\u5956\u52b1\u6765\u7684\u592a\u665a\uff0c\u592a\u4e0d\u786e\u5b9a\uff0c\u9700\u8981\u5f3a\u5927\u7684\u610f\u5fd7\u529b\u624d\u80fd\u6301\u7eed\u4e0b\u53bb\u3002\u6295\u5165\u4ea7\u51fa\u6bd4\u592a\u9ad8\u3002\\n\\n### \u77db\u76fe\\n\\n\u4e0a\u8ff0\u8fd9\u4e9b\u52a8\u4f5c\u90fd\u4f1a\u5bf9\u4e8e\u6211\u6765\u8bf4\u662f\u8f83\u5927\u7684\u5fc3\u667a\u8d1f\u62c5\u3002\u4e0d\u65ad\u5730\u5e72\u6270\u7740\u6211\u7684**_\u9605\u8bfb - \u7406\u89e3_**\u8fc7\u7a0b\u3002\u963b\u788d\u6211\u901a\u8fc7\u82f1\u8bed\u83b7\u53d6\u4fe1\u606f\u4e0e\u77e5\u8bc6\uff0c\u800c\u6211\u5f53\u524d\u6240\u671f\u671b\u83b7\u53d6\u7684\u4fe1\u606f\u4e0e\u77e5\u8bc6\uff0c\u5927\u90fd\u7531\u82f1\u8bed\u4e66\u5199\u800c\u6210\u3002\\n\\n## \u89e3\u51b3\u529e\u6cd5\\n\\n\u9762\u5bf9\u8fd9\u4e9b\u56f0\u96be\u3002\u6211\u7ecf\u8fc7\u79cd\u79cd\u601d\u8003\uff0c\u63a2\u7d22\uff0c\u5b9e\u8df5\u3002\u5f00\u53d1\u4e86\u8fd9\u6b3e\u540d\u4e3a Valo Reader \u7684\u5e94\u7528\u7a0b\u5e8f\u3002\u8fd9\u6b3e\u5e94\u7528\u7a0b\u5e8f\u901a\u8fc7 [OCR](https://zh.wikipedia.org/wiki/%E5%85%89%E5%AD%A6%E5%AD%97%E7%AC%A6%E8%AF%86%E5%88%AB) \u6280\u672f\uff0c\u5b9e\u65f6\u8bc6\u522b\u7528\u6237\u5f53\u524d\u6b63\u5728\u9605\u8bfb\u7684\u82f1\u6587\u6587\u672c\uff0c\u7ecf\u7531\u7a0b\u5e8f\u5185\u7f6e\u8bcd\u5178\u548c\u7528\u6237\u8bbe\u7f6e\uff0c\u5728\u7528\u6237\u544a\u77e5\u89c6\u89c9\u7126\u70b9\u540e\uff0c\u5c55\u793a\u5355\u8bcd\u91ca\u4e49\u3002\u5728\u8fd9\u6b3e\u5e94\u7528\u7a0b\u4e2d\uff0c\u6211\u671f\u671b\u63a5\u8fd1\u53ef\u80fd\u5730\u964d\u4f4e\u7528\u6237\u5728\u9605\u8bfb\u82f1\u6587\u8bed\u6599\u65f6\uff0c\u56e0\u4e3a\u638c\u63e1\u7684\u82f1\u8bed\u8bcd\u6c47\u91cf\u8f83\u5c11\uff0c\u800c\u4ea7\u751f\u7684\u989d\u5916\u5fc3\u667a\u8d1f\u62c5\u3002\\n\\n\ud83c\udf89 \u6211\u5e0c\u671b\uff0c\u6211\u4eec\u5728\u4f7f\u7528\u8fd9\u6b3e\u8f6f\u4ef6\u9605\u8bfb\u82f1\u6587\uff0c\u4e0d\u9700\u8981\u6709\u5355\u8bcd\u57fa\u7840\u4e86\uff0c\u8fd9\u53ef\u4ee5**\u5927\u5e45\u51cf\u5c11**\u751f\u758f\u5355\u8bcd\u5e26\u6765\u7684\u5fc3\u667a\u8d1f\u62c5\u3002\u52a9\u529b\u6211\u4eec\u7684**_\u9605\u8bfb - \u7406\u89e3_**\u8fc7\u7a0b\u3002\\n\\n![AppIcon](/img/logo.png)\\n\\n:::note\\n\u60f3\u8981\u5728\u4e0d\u540c\u5e73\u53f0\u4e0a\u83b7\u53d6 Valo Reader\uff0c\u8bf7\u53c2\u9605[\u5b89\u88c5](/docs/installation)\\n:::"}]}')}}]);